var ts = new Date();
var slot96 = Math.floor((ts.getHours() * 60 + ts.getMinutes()) / 15);
// console.log(ts.toString() + '--' + slot96)

// loop on all rooms
for(var i in context.global.rooms) {
    var room = context.global.rooms[i];

    if(room.actor !== undefined)
    {
        // there is something to control?

        if(room.timestamp === undefined) {
            // no data
            // TODO or too old data !
            outputMsgs.push( { payload: room, action:"error", error:"NO_SENSOR_DATA" });
        }
        else
        {
            var lastts = context.global.parseISO8601(room.timestamp);
            //console.log(lastts.toLocaleString()
            //    + ' ' + room.id + ' <=> ' + room.name);
            // 20min late = batteries dead? 
            if((lastts.getTime() + 1000*60*20) < ts.getTime())
                outputMsgs.push( { payload: room, action:"error", error:"OUTDATED_SENSOR_DATA" });

            if(room.actor == 14 || room.actor == 15) { // salon
                setSwitch(outputMsgs, ts, room.id, room.actor, room.value, 19, room.heater, room.name);

            } else if(room.actor == 6 || room.actor == 7) { // Ch.Bas
                // 7h30 -> 9h30
                // 21h15  -> 23h
                if((slot96 >= 30 && slot96 <= 38)||(slot96 >= 85 && slot96 <= 92)) {
                    // Hot
                    setSwitch(outputMsgs, ts, room.id, room.actor, room.value, 19.5, room.heater, room.name);
                } else {
                    // Cold
                    setSwitch(outputMsgs, ts, room.id, room.actor, room.value, 18, room.heater, room.name);
                }
            } else {
                // 7h30 -> 9h30
                // 18h  -> 23h
                if((slot96 >= 30 && slot96 <= 38)||(slot96 >= 72 && slot96 <= 92)) {
                    // Hot
                    setSwitch(outputMsgs, ts, room.id, room.actor, room.value, 19.5, room.heater, room.name);
                } else {
                    // Cold
                    setSwitch(outputMsgs, ts, room.id, room.actor, room.value, 18, room.heater, room.name);
                }
            }

            //outputMsgs.push( { payload: context.global.links[item], temp: curValue, switch: stateHeater, action:"processed" });
        }
    }
}
return [ outputMsgs ];

